import datetime
from jwt import decode, encode, ExpiredSignatureError
from flask import Flask, render_template, request, jsonify, redirect, url_for
import pandas as pd

data = pd.read_csv("C:\\Users\\Brian Hu\\Desktop\\temp_folder\\pd_write_data\\table.csv")
data = pd.DataFrame.to_json(data)

key = "secret"
algorithm = "HS256"
app = Flask(__name__)

user_database = dict()

def to_integer(time):
    ans = ""
    for char in str(time):
        if char.isnumeric():
            ans += char
        elif char == ".":
            return int(ans)

@app.route('/login', methods=["POST", "GET"])
def login():
    if request.method == "POST":
        if request.values["send"] == "submit":

            name = request.values["user"]
            password = request.values["password"]
            # next_url = request.values["next"]
            # convert start time to int type expression, then put it in values of "exp"
            start = to_integer(datetime.datetime.utcnow() + datetime.timedelta(minutes=2))
            access_token = encode({"user": name,
                                   "exp": start},
                                  key, algorithm)
            if name not in user_database:
                user_database[name] = password
            else:
                if password != user_database[name]:
                    return jsonify("wrong password")
                # if next_url:
                #     next
                #     return jsonify({"new token": access_token,
                #                     "next_url": next_url})
                # return redirect(url_for('home'))
                # else:
                #     return redirect(url_for('getData', token=access_token))

            return jsonify({"access token": access_token})
    return render_template("login.html")


