@app.route('/getData', methods=["GET"])
def getData():
    access_token = request.args["token"]
    #return access_token
    if not access_token:
        return jsonify('please login first!')
    access_token = decode(access_token, key, algorithm)
    limit = access_token["exp"]
    if to_integer(datetime.datetime.utcnow()) <= limit:
        return jsonify({"data": data})
    return redirect(url_for('login', next=request.url))
    

    # workdir = request.values["workdir"]
    # return jsonify({"access_token": refresh_token})
    

if __name__ == "__main__":
    app.run(debug=True)

